// Step 1: Log in
cy.loginHydralytica();
// Step 2: Wait for Dashboard
cy.contains('Dashboard', {
  timeout: 10000
}).should('be.visible');
// Step 3: Visit the organization creation page
cy.visit('https://hydralytica.com/organizations/create');
// Step 4: Fill in Organization Name
cy.get('input[name="name"]', {
  timeout: 10000
}).type('My Cypress Organization');
// Step 5: Select 4th item ("Corporate") from hidden <select>
cy.get('select[aria-hidden="true"]', {
  timeout: 10000
}).eq(0).select('Corporate', {
  force: true
});
// Step 6: Select Parent Organization (second dropdown)
cy.get('select[aria-hidden="true"]', {
  timeout: 10000
}).eq(1) // select the second dropdown
.find('option').contains('dynaCERT Inc.').then($option => {
  const val = $option.attr('value');
  cy.get('select[aria-hidden="true"]').eq(1).select(val, {
    force: true
  });
});
// Step 7: Fill in Notes (Optional)
cy.get('textarea[name="notes"]', {
  timeout: 10000
}).type('Test org created by Cypress.');
// Step 8: Fill in Contact Email
cy.get('input[name="contactFields.contact"]', {
  timeout: 10000
}).type('contact@example.com');
// Step 9: Fill in Contact Description (Optional)
cy.get('textarea[name="contactFields.description"]', {
  timeout: 10000
}).type('Main contact email.');
cy.contains('button', 'Add').eq(0).should('be.visible').click({
  force: true
});
// Step 10: Country
cy.get('input[name="addressFields.country"]').type('Canada');
// Step 11: City
cy.get('input[name="addressFields.city"]').type('Toronto');
// Step 12: Address
cy.get('input[name="addressFields.address"]').type('123 Queen Street');
// Step 13: Zip Code
cy.get('input[name="addressFields.zipCode"]').type('M5H 2N2');
// Step 14: Reference
cy.get('input[name="addressFields.reference"]').type('Suite 500');
// Step 15: Address Description
cy.get('input[name="addressFields.description"]').type('Head office location.');
// Click the second "Add" button (e.g., for address)
cy.wait(1000);
// Ensure button is enabled then click "Add"
cy.contains('div[data-slot="card-title"]', 'Address').parents('[data-slot="card"]').within(() => {
  cy.contains('button', 'Add').click();
});
// Step 16: Click Create
cy.contains('button', 'Create', {
  timeout: 10000
}).click();
